import React from 'react';
import axios from 'axios';
import { newExpression } from '@babel/types';
import Button from '@material-ui/core/Button';

class Create extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      Chlorine: '',
      Ph: ''
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleMovieChlorineChange = this.handleMovieChlorineChange.bind(this);
    this.handleMoviePhChange = this.handleMoviePhChange.bind(this);
  }

  handleMovieChlorineChange(e) {
    this.setState({ Chlorine: e.target.value });
  }

  handleMoviePhChange(e) {
    this.setState({ Ph: e.target.value });
  }

  handleSubmit(e) {
    alert(this.state.Chlorine + "      " + this.state.Ph);
    e.preventDefault();


    const newMovie = {
      chlorine: this.state.Chlorine,
      ph: this.state.Ph
    };
    axios.post('http://localhost:4000/api/movies', newMovie)
      .then()
      .catch();


    this.setState({
      Chlorine: '',
      Ph: ''
    });
  }

  render() {
    return (
      <div>
        <h1>Hello from Create component</h1>
        <form onSubmit={this.handleSubmit}>
          <div className='form-group'>
            <label> Chlorine</label>
            <input
              type='text'
              className='form-control'
              value={this.state.Chlorine}
              onChange={this.handleMovieChlorineChange}
            ></input>
          </div>
          <div className='form-group'>
            <label> Ph</label>
            <input
              type='text'
              className='form-control'
              value={this.state.Ph}
              onChange={this.handleMoviePhChange}
            ></input>
          </div>
          <div>
            <Button
              varient="contained"
              color="primary"
              type="submit"
              value="Add Movie">
            </Button>
          </div>
        </form>
      </div>
    );
  }
}

export default Create;
