import React, { Component } from 'react';
import '../App.css';
import { STATES } from 'mongoose';
import Axios from 'axios';
class Index extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      apiResults: []
    };
  }

  componentDidMount() {
    Axios.get('http://api.openweathermap.org/data/2.5/weather?q=Galway&appid=d24d19a0fa6b2334b4039e272b3c79ef')
      .then(response => {
        this.setState({ apiResults: response.data.data });
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  render() {
      const apiResults = this.state.apiResults.map((apiResults, key) => {
        return <div key={key}>
          <h4>{apiResults.weather.description}</h4>
        </div>

      });


      return (
        <div>
          <ul>
            {apiResults}
            <h2>It is {new Date().toLocaleTimeString()}.</h2>
          </ul>
        </div>
      );   
  }

}


export default Index;
