import React from 'react'
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import { isTemplateElement } from '@babel/types';

class Index extends React.Component{
constructor(props){
    super(props);
   this.state = {
        ApiWeather: []
    };
}

    componentDidMount() {
        axios.get('http://api.openweathermap.org/data/2.5/weather?q=Galway&appid=d24d19a0fa6b2334b4039e272b3c79ef')
        .then((response)=>{
            this.setState({ApiWeather: response.data.weather})
        })
        .catch((error)=>{
            console.log(error);
        });
    }

    render() {
        var{ApiWeather} = this.state;
        return (
          <ul>
{ApiWeather.map(weather=>  (


<li key={weather.id}>
<div>
<h2>Weather</h2>
{weather.description}
<br></br>
</div>




</li>

))}
          </ul>
        );
      }
    }

export default Index;
