import React from 'react';
import axios from 'axios';

class Edit extends React.Component{
    constructor(props){
        super(props);

        this.state = {Chlorine:'',
                      Year:'',
                      _id:''};

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleMovieChlorineChange = this.handleMovieChlorineChange.bind(this);
        this.handleMovieYearChange = this.handleMovieYearChange.bind(this);
      }
componentDidMount(){
    alert(this.props.match.params.id);

    axios.get('http://localhost:4000/api/movies/'+this.props.match.params.id)
    .then((response)=>{
        this.setState({
            _id:response.data._id,
            Chlorine:response.data.chlorine,
            Year:response.data.year
        })
    })
    .catch();


}
handleMovieChlorineChange(e){
    this.setState({Chlorine: e.target.value});
  }

  handleMovieYearChange(e){
    this.setState({Year: e.target.value});
  }

  handleSubmit(e){
    alert(this.state.Chlorine+ "      " + this.state.Year);
    e.preventDefault();

    const newMovie = {
      chlorine: this.state.Chlorine,
        year: this.state.Year
    };

    axios.put('http://localhost:4000/api/movies/'+this.state._id, 
    newMovie)
    .then()
    .catch();


    this.setState({Chlorine:'',
                    Year:''});    
  }


    render(){
        return(
            <div>
                <h1>Hello from Edit component</h1>
                <form onSubmit={this.handleSubmit}>
        <div className='form-group'>
          <label>Movie Chlorine</label>
          <input
          type='text'
          className='form-control'
          value={this.state.Chlorine}
          onChange={this.handleMovieChlorineChange}
          ></input>
        </div>
        <div className='form-group'>
          <label>Movie Year</label>
          <input
          type='text'
          className='form-control'
          value={this.state.Year}
          onChange={this.handleMovieYearChange}
          ></input>
        </div>
        <div>
          <input
          type="submit"
          value="Edit Movie">
          </input>
        </div>
        </form>
            </div>
        )
    }
}

export default Edit;
