import React from 'react';
import axios from 'axios';

class Edit extends React.Component{
    constructor(props){
        super(props);

        this.state = {Chlorine:'',
        Ph:'',
                      _id:''};

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleMovieChlorineChange = this.handleMovieChlorineChange.bind(this);
        this.handleMoviePhChange = this.handleMoviePhChange.bind(this);
      }
componentDidMount(){
    alert(this.props.match.params.id);

    axios.get('http://localhost:4000/api/movies/'+this.props.match.params.id)
    .then((response)=>{
        this.setState({
            _id:response.data._id,
            Chlorine:response.data.chlorine,
            Ph:response.data.ph
        })
    })
    .catch();


}
handleMovieChlorineChange(e){
    this.setState({Chlorine: e.target.value});
  }

  handleMoviePhChange(e){
    this.setState({Ph: e.target.value});
  }

  handleSubmit(e){
    alert(this.state.Chlorine+ "      " + this.state.Ph);
    e.preventDefault();

    const newMovie = {
      chlorine: this.state.Chlorine,
        ph: this.state.Ph
    };

    axios.put('http://localhost:4000/api/movies/'+this.state._id, 
    newMovie)
    .then()
    .catch();


    this.setState({Chlorine:'',
    Ph:''});    
  }


    render(){
        return(
          <div className="App-header">
                <h1>Hello from Edit component</h1>
                <form onSubmit={this.handleSubmit}>
        <div className='form-group'>
          <label>Movie Chlorine</label>
          <input
          type='text'
          className='form-control'
          value={this.state.Chlorine}
          onChange={this.handleMovieChlorineChange}
          ></input>
        </div>
        <div className='form-group'>
          <label>Ph</label>
          <input
          type='text'
          className='form-control'
          value={this.state.Ph}
          onChange={this.handleMoviePhChange}
          ></input>
        </div>
        <div>
          <input
          type="submit"
          value="Edit Movie">
          </input>
        </div>
        </form>
            </div>
        )
    }
}

export default Edit;
