import React from 'react'
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import { isTemplateElement } from '@babel/types';

class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ApiWeather: [],
      ApiMain: []
    };
  }

  componentDidMount() {
    axios.get('http://api.openweathermap.org/data/2.5/weather?q=Galway&appid=d24d19a0fa6b2334b4039e272b3c79ef')
      .then((response) => {
        this.setState({ ApiWeather: response.data.weather })
        this.setState({ ApiWeather: response.data.main })
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    var { ApiWeather } = this.state;
    var {ApiMain} = this.state
    return (

      <ul className="App-header">
        {ApiWeather.map(weather => (
          <li key={weather.id}>
            <div>
            <h2>It is {new Date().toLocaleTimeString()}.</h2>
              <h2>Todays Weather</h2>
              <h3>{weather.description}</h3>
              <br></br>
            </div>
          </li>

        ))}
      </ul>

    );
  }
}

export default Index;
