import React from 'react';
import axios from 'axios';
import { newExpression } from '@babel/types';
import Button from '@material-ui/core/Button';

class Create extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      Chlorine: '',
      Ph: ''
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleMovieChlorineChange = this.handleMovieChlorineChange.bind(this);
    this.handleMoviePhChange = this.handleMoviePhChange.bind(this);
  }

  handleMovieChlorineChange(e) {
    this.setState({ Chlorine: e.target.value });
  }

  handleMoviePhChange(e) {
    this.setState({ Ph: e.target.value });
  }

  handleSubmit(e) {
    alert(this.state.Chlorine + "      " + this.state.Ph);
    e.preventDefault();


    const newMovie = {
      chlorine: this.state.Chlorine,
      ph: this.state.Ph
    };
    axios.post('http://localhost:4000/api/movies', newMovie)
      .then()
      .catch();


    this.setState({
      Chlorine: '',
      Ph: ''
    });
  }

  render() {
    return (
          <div className="App-header">
          <h2> {new Date().toLocaleTimeString()}</h2>
          <form onSubmit={this.handleSubmit}>
              <div className='form-group'>
                  <label>Please enter your chlorine value : </label>
                  <input
                      type='text'
                      value={this.state.Chlorine}
                      className='form-control'
                      onChange={this.handleMovieChlorineChange}>
                  </input>
              </div>
              <br></br>

              <div className='form-group'>
                  <label>Please enter your Ph value : </label>
                  <input
                      type='text'
                      value={this.state.Ph}
                      className='form-control'
                      onChange={this.handleMoviePhChange}
                  ></input>
              </div>

              <br></br>
              <div>

                  <Button
                      variant="contained"
                      color="primary"
                      type="submit"
                      value="Submit results">
                      Submit
                  </Button>
              </div>
        </form>
      </div>
    );
  }
}

export default Create;
