import React, { Component } from 'react';
import '../App.css';
import { STATES } from 'mongoose';
class Index extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      dataSource: null,
    }
  }


  componentDidMount() {
    return fetch('http://api.openweathermap.org/data/2.5/weather?q=Galway&appid=d24d19a0fa6b2334b4039e272b3c79ef')
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({
          isLoading: false,
          dataSource: responseJson.data,
        })

      })

      .catch((error) => {
        console.log(error)
      });
  }


  render() {
    return (
      <div className="App-header">
        <h1>Hello World from Index</h1>
        <h2>It is {new Date().toLocaleTimeString()}.</h2>
      </div>
    );
  }

  render() {

    if (this.state.isLoading) {
      return (
        <div></div>
      )
    } else {

      let weather = this.state.dataSource.map((val, key) => {
        return <div key={key}>
          <h1>{val.summary}</h1>
        </div>
      });


      return (

        <div className="App-header">
          <div>
            <h1>{weather}</h1>
          </div>

        </div>

      );
    }
  }
}


export default Index;
