import React, { Component } from 'react';
import '../App.css';
import Axios from 'axios';
class Index extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      Weather: []
    };
  }

  componentDidMount() {
    Axios.get('http://api.openweathermap.org/data/2.5/weather?q=Galway&appid=d24d19a0fa6b2334b4039e272b3c79ef')
      .then(response => {
        this.setState({ Weather: response.weather });
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  render() {
    return (
      <div className="App-header">
        <h2>It is {new Date().toLocaleTimeString()}.</h2>
      </div>
    )
  }
