import React from 'react'
import axios from 'axios';
import Carousel from 'react-bootstrap/Carousel'
import watersafety from "../images/watersafety.png";

function ControlledCarousel() {
  const [index, setIndex] = useState(0);
  const [direction, setDirection] = useState(null);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
    setDirection(e.direction);
  };
}

class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ApiWeather: []
    };
  }

  componentDidMount() {
    //Using axios to connect to the Api and recieve the data requested
    axios.get('http://api.openweathermap.org/data/2.5/weather?q=Galway&appid=d24d19a0fa6b2334b4039e272b3c79ef')
      .then((response) => {
        this.setState({ ApiWeather: response.data.weather })
      })
      .catch((error) => {
        console.log(error);
      });
  }

  //Printing out my weather Api into a list
  render() {
    var { ApiWeather } = this.state;
    return (
      <ul className="App-header">
        {ApiWeather.map(weather => (
          <li>
            <div>
              <h2>It is {new Date().toLocaleTimeString()}.</h2>
              <h2>Todays Weather</h2>
              <h3>{weather.description}</h3>
              <br></br>
              <h4><i>Remember always be safe near the water!</i></h4>
              <img src={watersafety} alt="Irish Water Safety.png"/>
              <Carousel activeIndex={index} direction={direction} onSelect={handleSelect}>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=First slide&bg=373940"
          alt="First slide"
        />
        <Carousel.Caption>
          <h3>First slide label</h3>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=Second slide&bg=282c34"
          alt="Second slide"
        />

        <Carousel.Caption>
          <h3>Second slide label</h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=Third slide&bg=20232a"
          alt="Third slide"
        />

        <Carousel.Caption>
          <h3>Third slide label</h3>
          <p>
            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
          </p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
            </div>
          </li>
        ))}
      </ul>
    );
  }
}

export default Index;
