import React, { Component } from 'react';
import '../App.css';
import { STATES } from 'mongoose';
import Axios from 'axios';
class Index extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      games: []
    };
  }

  componentDidMount() {
    Axios.get('http://api.openweathermap.org/data/2.5/weather?q=Galway&appid=d24d19a0fa6b2334b4039e272b3c79ef')
      .then(response => {
        this.setState({ games: response.data.data });
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  render() {
    if (this.props.data) {
      const games = this.state.games.map((games, key) => {
        return <div key={key}>
          <h4>{games.weather.description}</h4>
        </div>

      });


      return (
        <div>
          <ul>
            {games}
          </ul>
        </div>
      );
    }
  }
