import React from 'react'
import axios from 'axios';

import Button from '@material-ui/core/Button';

class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ApiWeather: [],
      ApiWind: [],
      Location: String
    };
    this.handleLocationChange = this.handleLocationChange.bind(this);

  }

  handleLocationChange(e) {
    this.setState({ Location: e.target.value });
  }

  handleSubmit(e) {
    alert("Chlorine: " + this.state.Chlorine + " ph: " + this.state.Ph + " Name: " + this.state.Name);
    e.preventDefault();


    const newLocation = {
      location: this.state.Location
    };

    this.setState({
      Location: ''
    });
  }


  // axios.get('http://api.openweathermap.org/data/2.5/weather?q=Galway&appid=d24d19a0fa6b2334b4039e272b3c79ef')


  componentDidMount() {
    axios.get('http://api.openweathermap.org/data/2.5/weather?q=' + this.state.Location + '&appid=d24d19a0fa6b2334b4039e272b3c79ef')
      .then((response) => {
        this.setState({ ApiWeather: response.data.weather })
        this.setState({ ApiWind: response.data.wind })
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    var { ApiWeather } = this.state;
    return (
      <div>
        <ul className="App-header">
          {ApiWeather.map(weather => (
            <li>
              <div>
                <h2>It is {new Date().toLocaleTimeString()}.</h2>
                <h2>Todays Weather</h2>
                <h3>{weather.description}</h3>
                <br></br>
              </div>
            </li>
          ))}
        </ul>

        <div className='form-group'>
          <form onSubmit={this.handleSubmit}>
            <label>Please enter your City: </label>
            <input
              type='text'
              value={this.state.Location}
              className='form-control'
              onChange={this.handleLocationChange}>

              <Button
                variant="contained"
                color="primary"
                type="submit"
                value="Submit results">
                Submit
            </Button>
            </input>
          </form>
        </div>
      </div>

    );
  }
}

export default Index;
